mapper.select:
SELECT board_id, member_no, title, content, author, creation_date, view_count
FROM bulletin_board
WHERE board_id = #{boardId}

mapper.update:
UPDATE bulletin_board
SET view_count = view_count + 1
WHERE board_id = #{boardId}

UPDATE bulletin_board
SET title = #{title}, content = #{content}
WHERE board_id = #{boardId}

mapper.select:
SELECT reply_id, board_id, member_no, author, creation_date, reply_title, reply_content, reply_ref, reply_step, reply_level
FROM replies
WHERE board_id = #{boardId}
ORDER BY reply_ref ASC, reply_step ASC;

SELECT COALESCE(MAX(reply_ref), 0)
FROM replies
WHERE reply_level = 0 AND board_id = #{boardId}
