mapper.select:
SELECT user_id AS userId, company_name AS companyName, email, phone_number AS phoneNumber, password
FROM users
WHERE user_id = #{id}

SELECT user_id AS userId, company_name AS companyName, email, phone_number AS phoneNumber, password
FROM users
WHERE company_name = #{companyName} AND email = #{email}

SELECT user_id AS userId, company_name AS companyName, email, phone_number AS phoneNumber, password
FROM users
WHERE company_name = #{companyName} AND phone_number = #{phone}
