mapper.update:
UPDATE members
SET password = #{password}
WHERE id = #{id}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END
FROM members
WHERE id = #{id}

SELECT COUNT(*) > 0
FROM users
WHERE email = #{email}

SELECT COUNT(*) > 0
FROM users
WHERE phone_number = #{phoneNumber}

mapper.update:
UPDATE members
SET session_id = #{sessionId},
    last_access_time = #{lastAccessTime},
    session_expiry_time = #{expiryTime}
WHERE id = #{id}
